def palindromePairs(self, words: List[str]) -> List[List[int]]:

    def isPalindrome(word: str) -> bool:
        for i in range(len(word) >> 1):
            if word[i] != word[~i]:
                return False
        return True
    # end isPalindrome

    words_dict: Dict[str,int] = {word[::-1]:i for i, word in enumerate(words)} # map reversed words to their indices
    ans: List[List[int]] = []

    for i in range(len(words)):

        # analyze the whole words[i]
        if words[i] in words_dict:
            if i != words_dict[words[i]]:
                # words[i] is not a palindrome, but reversed word exists in `words`,
                # thus joining words[i] with reversed word them will give a palindrome
                ans.append([i,words_dict[words[i]]])
            elif len(words[i]) and "" in words_dict:
                # words[i] is non-empty string palindrome and "" is in `words`,
                # so adding "" to the beginning or to the end of words[i] gives a palindrome
                ans.append([words_dict[""],i])
                ans.append([i,words_dict[""]])

        # analyze prefixes and suffixes
        for j in range(1,len(words[i])):
            pref = words[i][:j]
            suff = words[i][j:]
            if suff in words_dict and isPalindrome(pref): # check if suff in words_dict first, palindrome check is expensive
                    ans.append([words_dict[suff],i])
            if pref in words_dict and isPalindrome(suff):
                    ans.append([i, words_dict[pref]])
    return ans
# end palindromePairs()
