        while (i < j) {
            if (word[i] != word[j]) {
                return false;
            }
            i++;
            j--;
        }
        return true;
}
vector<vector<int>> palindromePairs(vector<string>& words) {
        vector<vector<int>> result;
        unordered_map<string, int> wordMap;
        for (int i = 0; i < words.size(); i++) {
            wordMap[words[i]] = i;
        }
        for (int i = 0; i < words.size(); i++) {
            string word = words[i];
            string reversedWord = word;
            reverse(reversedWord.begin(), reversedWord.end());
            if (wordMap.find(reversedWord) != wordMap.end() && wordMap[reversedWord] != i) {
                result.push_back({i, wordMap[reversedWord]});
            }
            if (isPalindrome(word)) {
                string emptyString = "";
                if (wordMap.find(emptyString) != wordMap.end() && wordMap[emptyString] != i) {
                    result.push_back({i, wordMap[emptyString]});
                    result.push_back({wordMap[emptyString], i});
                }
            }
            for (int j = 1; j < word.size(); j++) {
                string left = word.substr(0, j);
                string right = word.substr(j);
                string reversedLeft = left;
                string reversedRight = right;
                reverse(reversedLeft.begin(), reversedLeft.end());
                reverse(reversedRight.begin(), reversedRight.end());
                if (isPalindrome(left) && wordMap.find(reversedRight) != wordMap.end()) {
                    result.push_back({wordMap[reversedRight], i});
                }
                if (isPalindrome(right) && wordMap.find(reversedLeft) != wordMap.end()) {
                    result.push_back({i, wordMap[reversedLeft]});
                }
            }
        }
        return result;
	}
```
Time Complexity: O(n * k^2) where n is the number of words and k is the length of the longest word
Space Complexity: O(n * k)

The only way to optimize the code further is to use TRIE.
**Approach:**

Idea is to use Trie to store all the words in reverse order
So that we can find all the palindromes in O(k) time
C++(NO TLE, ACCEPTED CODE):

```
struct TrieNode {
    TrieNode *next[26] = {};
    int index = -1;
    vector<int> palindromeIndexes;
};
class Solution {
    bool isPalindrome(string &s, int i, int j) {
        while (i < j && s[i] == s[j]) ++i, --j;
        return i >= j;
    }
    TrieNode *root;
    void insert(string &s, int index) {
        TrieNode *node = root;
        for (int i = s.size() - 1; i >= 0; i--) {
            int c = s[i] - 'a';
            if (!node->next[c]) {
                node->next[c] = new TrieNode();
            }
            if (isPalindrome(s, 0, i)) {
                node->palindromeIndexes.push_back(index);
            }
            node = node->next[c];
        }
        node->index = index;
        node->palindromeIndexes.push_back(index);
    }
    public:
    vector<vector<int>> palindromePairs(vector<string>& words) {
        root = new TrieNode();
        for (int i = 0; i < words.size(); i++) {
            insert(words[i], i);
        }
        vector<vector<int>> result;
        for (int i = 0; i < words.size(); i++) {
            TrieNode *node = root;
            string &s = words[i];
            for (int j = 0; j < s.size(); j++) {
                if (node->index >= 0 && node->index != i && isPalindrome(s, j, s.size() - 1)) {
                    result.push_back({i, node->index});
                }
                node = node->next[s[j] - 'a'];
                if (!node) break;
            }
            if (node) {
                for (int j : node->palindromeIndexes) {
                    if (i == j) continue;
                    result.push_back({i, j});
                }
            }
        }
        return result;
    }
};
