std::vector<std::vector<int>> palindromePairs(std::vector<std::string>& words) {
	const int n = words.size();
	std::vector<std::vector<int>> result;
	std::unordered_map<std::string, int> map_;
	for (int i = 0; i < n; i++) {
		map_[words[i]] = i;
	}
	for (int i = 0; i < n; i++) {
		int left = 0, right = 0;
		while (left <= right) {
			std::string temp = words[i].substr(left, right - left);
			std::reverse(temp.begin(), temp.end());
			std::string valid_substr = words[i].substr(left == 0 ? right : 0, 
													   left == 0 ? words[i].size() - right : left);
			if (map_.count(temp) && i != map_[temp] && isValid(valid_substr)) {
				if (left == 0) {
					result.push_back({i, map_[temp]});
				} 
				else result.push_back({map_[temp], i});
			}
			if (right < words[i].size()) {
				right++;
			} 
			else left++;
		}
	}
	return result;
}
bool isValid(std::string temp) {
	const int n = temp.size();
	for (int i = 0; i < n; i++) {
		if (temp[i] != temp[n - 1 - i]) return false;
	}
	return true;
}
