class Trie {

    private TrieNode root;

    Trie(){
        this.root = new TrieNode();
    }

    // Add words to the trie
    public void add(String word, int currWordIdx){
        TrieNode currNode = root;

        for(int i=0; i < word.length(); i++){
            // If the substring from the ith index is a palindrome, then we add the current index of the word
            // to the list of palindromeIdxs for this particular node
            if(isRemainingStrPalindrome(word, i))
                currNode.palindromeIdxs.add(currWordIdx);

            // Add the current char in Trie if it already doesn't exist
            char c = word.charAt(i);
            if(!currNode.nodes.containsKey(c))
                currNode.nodes.put(c, new TrieNode());
            currNode = currNode.nodes.get(c);
        }
        currNode.wordIdx = currWordIdx;
    }

    // Search for pairable Palindromes in Trie with current word and add valid palindrome pairs in the result List
    public void search(String word, int currWordIdx, List<List<Integer>> result){
        TrieNode currNode = root;

        // Case 1: Prefix of current word is present in trie & remaining word is a Palindrome
        // 
        // Iterate through all the characters in the word and search for the corresponding character in the trie
        // - At any point when we don't find the next character in the Trie we break from the loop.
        // - If the substring of the word(s1) from the current character is a palindrome, 
        //   and the current Node forms a word(s2) in the trie, then s1 + s2 is a palindrome
        for(int i=0; i < word.length(); i++){            
            if(isRemainingStrPalindrome(word, i) && currNode.wordIdx != -1)
                result.add(List.of(currWordIdx, currNode.wordIdx));
            char c = word.charAt(i);
            currNode = currNode.nodes.get(c);
            if(currNode == null)
                break;
        }

        // If we couldn't find a match in the trie, then the current word cannot be paired with any other words to form a palindrome
        if(currNode == null)
            return;

        // When we have iterated through all the characters in current word and found the same in Trie, 
        // then only Case 2 & Case 3 will be executed

        // Case 2: When mirror image of current word exists
        //
        // - When an exact String is present in Trie (i.e. exact reverse of current word) 
        //   that doesn't match the index of the current word, then the 2 words when concatenated will form a palindrome
        if(currNode.wordIdx != -1 && currNode.wordIdx != currWordIdx)
            result.add(List.of(currWordIdx, currNode.wordIdx));

        // Case 3: Suffixes of the current Trie Node are palindromes
        // 
        // Because we have come this far, that means current TrieNode char matches with last char in current word
        // When there are palindromes from the current TrieNode, the indexes of which we already stored when loading the trie, 
        // that means if we concatenate the current word with all these words it will also form a palindrome
        // Ex - 
        // String[] words = ["lls", "sssll", "sossll"]
        // When current word is "lls", since the Trie is storing the words in reverse order, so "sssll" will look like this in trie -
        //  root -> l -> l -> s -> s -> s
        //                    ^     \   
        //                           \-> o -> s 
        // Upto the pointed "s", we can match the current word "lls", and the remaining String "ss" and "sos" both are palindromes. 
        // So if we concatenate current word "lls" with "sssll" or "sossll", they'll form palindromes - "llssssll", "llssossll"
        for(int otherIdx : currNode.palindromeIdxs)
            result.add(List.of(currWordIdx, otherIdx));
    }

    private boolean isRemainingStrPalindrome(String word, int startIdx){        
        for(int i=startIdx, j=word.length()-1; i < j; i++, j--)
            if(word.charAt(i) != word.charAt(j))
                return false;
        return true;
    }
}

class TrieNode {
    Map<Character, TrieNode> nodes = new HashMap<>();
    int wordIdx = -1;
    List<Integer> palindromeIdxs = new ArrayList<>();
}
