public class Solution {
    private TrieNode root;
    public IList<IList<int>> PalindromePairs(string[] words) {
        this.root = new TrieNode();
        var res = new List<IList<int>>();
        for(int i = 0; i < words.Length; i++)
        {
            this.InsertWordReversely(words[i], i);
        }

        for (int i = 0; i < words.Length; i++)
        {
            this.Search(words[i], i, res);
        }

        return res;
    }

    private void Search(string word, int wordIndex, IList<IList<int>> res)
    {
        TrieNode cur = this.root;
        for (int i = 0; i < word.Length; i++)
        {
            char c = word[i];
            int index = this.GetCharIndex(c);

            //case 2: word 1是长的(xyzll)，word2是短的(zyx)，因为Trie里面是反向存word对应的也就是(xyz)，所以当在trie里面找到一个和word1 prefix完全相反的word后只需要看word1剩下的是否是个Palindrome
            if (cur.wordIndex != -1 && this.RestIsPalindrome(word, i, word.Length - 1))
            {
                res.Add(new List<int>() {wordIndex, cur.wordIndex});
            }

            cur = cur.chars[index];
            if (cur == null)
            {
                return;
            }
        }

        //case 1: word1 和 word 2长度一样，并且他们是palindrome,另外像aaa这种自己本身就是palindrome要去除(cur.wordIndex != wordIndex)
        if (cur.wordIndex != -1 && cur.wordIndex != wordIndex)
        {
            res.Add(new List<int>() {wordIndex, cur.wordIndex});
        }

        //case 3: word1是短的(xyz)，word2是长的(llzyx)，这个时候需要把word2里那些RestIsPalindrome的index也要加入
        foreach(int restIsPalindromeIndex in cur.restIsPalindrome)
        {
            res.Add(new List<int>() {wordIndex, restIsPalindromeIndex});
        }
    }

    private void InsertWordReversely(string word, int wordIndex)
    {
        TrieNode cur = this.root;
        for(int i = word.Length - 1; i >=0; i--)
        {
            char c = word[i];
            int index = this.GetCharIndex(c);

            if (this.RestIsPalindrome(word, 0, i))
            {
                cur.restIsPalindrome.Add(wordIndex);    
            }

            if (cur.chars[index] == null)
            {
                cur.chars[index] = new TrieNode();
            }

            cur = cur.chars[index];
        }

        cur.wordIndex = wordIndex;
    }

    private bool RestIsPalindrome(string word, int start, int end)
    {
        while (start < end)
        {
            if (word[start++] != word[end--])
            {
                return false;
            }
        }

        return true;
    }

    private int GetCharIndex (char c)
    {
        return c - 'a';
    }
}

