            int total = sum.pop();
            int begin = start.pop();
            if (total == target)
                result.add(res);
            else {
                for (int i = begin; i < candidates.length; i++) {
                    int t = total + candidates[i];
                    if (t > target)
                        break;
                    ArrayList<Integer> r = new ArrayList<Integer>(res);
                    r.add(candidates[i]);
                    stack.push(r);
                    sum.push(t);
                    start.push(i);
                }
            }
        }
        return result;
    }

