            if k==4 and len(s)==0:
                ans.append(temp[:-1])
                return
            if k==4 or len(s)==0:
                return 
            for i in range(3):
                if k>4 or i+1>len(s):
                    break
                if int(s[:i+1])>255:
                    continue
                if i!=0 and s[0]=="0":
                    continue
                back(s[i+1:],ans,k+1,temp+s[:i+1]+'.')  
            return ans
        return back(s,ans,k,'')
