class Solution(object):
    def combine(self, N, K):
        def dfs(min_num, path):
            if len(path)==K:
                opt.append(path)
                return
            else:
                for num in xrange(min_num, N+1):
                    dfs(num+1, path+[num])
        opt = []
        dfs(1, [])
        return opt
