    void solve(vector<int>& candidates, int target , int idx ,
    	vector<int>&currentAns , vector<vector<int>>&answer) {


    	//Base case 
    	if( target == 0 ) {
    		//if target is zero we have found the combination
    		answer.push_back(currentAns);
    		return;
    	}
    	if(idx >= candidates.size()) return;
