class Solution {
public:
    vector<vector<int>> combinationSum(vector<int>& candidates, int target) {
        sort(begin(candidates),end(candidates));
        int n=candidates.size();
        while(n&&(candidates[n-1]>target)){n--;}
        int sum=target;
        vector<vector<int>> result;
        vector<int> comb;
        findNextComboSum(candidates,result,comb,n,sum,0);
        return result;
    }

    bool findNextComboSum(vector<int>& candidates,vector<vector<int>>& res,
                         vector<int>& comb,int& n,int sum,int start){
        if(sum<0)return false;
        if(sum==0){res.push_back(comb);return true;}
        bool tryNext=true;
        for(int i=start;tryNext&&i<n;i++){
            comb.push_back(candidates[i]);
            tryNext=findNextComboSum(candidates,res,comb,n,sum-candidates[i],i);
            comb.pop_back();
        }
        return true;
    }
};
```
