class Solution {

	private void combinationSumUtil (int[] candidates, int target, int index, List<Integer> temp, List<List<Integer>> res) {

		if (target == 0) {
			res.add (new ArrayList <>(temp));
		}

		for (; index < candidates.length && target > 0; index++) {
			temp.add (candidates[index]);
			combinationSumUtil (candidates, target - candidates[index], index, temp, res);
			temp.remove (temp.size () - 1);
		}
	}

	public List<List<Integer>> combinationSum (int[] candidates, int target) {

		List<List<Integer>> res = new ArrayList <>();
		combinationSumUtil (candidates, target, 0, new ArrayList<Integer> (), res);

		return res;
	}
}
