        result.pop_back();
        combinationSumUtil(candidates, target, size-1);
    }
    vector<vector<int>> combinationSum(vector<int>& candidates, int target) {
        combinationSumUtil(candidates,target,candidates.size());
        return finalResult;
    }
};

