// My first code
class Solution {
    public List<List<Integer>> combinationSum(int[] nums, int target) {
        List<List<Integer>> ans = new ArrayList();
        recurse(nums, target, 0, 0, new ArrayList(), ans);
        return ans;
    }

    private void recurse(int[] nums, int target, int sum, int start, List<Integer> tempList, List<List<Integer>> ans) {
        if (sum == target) {
            ans.add(new ArrayList<>(tempList));
            return;
        }
        if (sum > target) return;

        for (int i = start; i < nums.length; ++i) {
            tempList.add(nums[i]);
            recurse(nums, target, sum + nums[i], i, tempList, ans);
            tempList.remove(tempList.size() - 1);
        }
    }
}
