class Solution {
    public boolean wordBreak(String s, List<String> wordDict) {
        Set<String> wordSet = new HashSet<>(wordDict);
        int[] maxmin = getMaxMin(wordDict);
        int maxLen = maxmin[0];
        int minLen = maxmin[1];
        boolean[] dp = new boolean[s.length() + 1];
        dp[0] = true;
        for (int i = 1; i <= s.length(); i++) {
            for (int l = Math.max(0, i - maxLen); l + minLen <= i; l++) {
                if (dp[l] && wordSet.contains(s.substring(l, i))) {
                    dp[i] = true;
                    break;
                }
            }
        }
        for (int l = 0; l < s.length(); l++)
            for (int len = minLen; len <= maxLen && l + len <= s.length(); len++)
                if (!dp[l + len] && dp[l] && wordSet.contains(s.substring(l, l + len)))
                    dp[l + len] = true;
        return dp[s.length()];
    }
    private int[] getMaxMin(List<String> list) {
        int max = 0, min = 0;
        for (String s : list) {
            max = Math.max(max, s.length());
            min = Math.min(min, s.length());
        }
        return new int[]{max, min};
    }
}
```

