int dp[302][302];

    int wordBreakHelper(string A, vector<string> B, int i, int j, unordered_set<string> us)
    {
        if(i>j)
            return 0;

        if(dp[i][j] != -1)
            return dp[i][j];

        bool ans = 0, tempAns;

        if(us.find(A.substr(i, j-i+1)) != us.end())
            return 1;

        for(int k=i;k<j;k++)
        {
            int leftChoice;
            if(dp[i][k]!= -1)
                leftChoice = dp[i][k];
            else
                dp[i][k] = leftChoice = wordBreakHelper(A, B, i, k, us);

            int rightChoice;
            if(dp[k+1][j]!= -1)
                rightChoice = dp[k+1][j];
            else
                dp[k+1][j] = rightChoice = wordBreakHelper(A, B, k+1, j, us);

            tempAns = leftChoice && rightChoice;
            ans = ans || tempAns;
        }

        return dp[i][j] = ans;
    }

    bool wordBreak(string s, vector<string>& wordDict) {

        memset(dp, -1, sizeof(dp));

		unordered_set<string> us(wordDict.begin(), wordDict.end());

        return wordBreakHelper(s, wordDict, 0, s.size()-1, us);
    }
