class Trie {
public:
    unordered_map<char, Trie*> children;
    bool isEnd=false;

    void insert(string word) {
        Trie* node=this;
        for(char c : word){
            if(node->children.find(c)==node->children.end())
                node->children[c]=new Trie();
            node=node->children[c];
        }
        node->isEnd=true;
    }

    bool search(string word) {
        Trie* node=this;
        for(char c : word){
            if(node->children.find(c)==node->children.end())
                return false;
            node=node->children[c];
        }
        return node->isEnd;
    }
};

