class Solution:
    def wordBreak(self, s: str, wordDict: List[str]) -> bool:
        n = len(s)
        stack = []
        stack.append(0)
        visited = [False]*(n+1)
        while(stack):
            idx = stack.pop()
            if not visited[idx]:
                for i in range(idx,n):
                    if s[idx:i+1] in wordDict:
                        if s[i+1:] == '' or s[i+1:] in wordDict:
                            return True
                        stack.append(i+1)
            visited[idx] = True
        return False
