class Solution {
    int dp[301];
    //top-down approach
    int helper(int i, string s, set<string>&st)
    {
        if(i==s.size())
            return 1;
        string temp;
        if(dp[i]!=-1)
            return dp[i];
        for(int j=i;j<s.size();j++)
        {
            temp+=s[j];
            if(st.find(temp)!=st.end())
            {
                if(helper(j+1,s,st))
                    return dp[i]=1;
            }
        }
        return dp[i]=0;
    }
