class Solution {
private:
    struct Node {
        bool isValid;
        Node* child[256];
    };
public:
    bool wordBreak(string s, vector<string>& wordDict) {
        Node* root = new Node();
        for (auto& word : wordDict) {
            Node* tmp = root;
            for (auto& c : word)
                tmp = (tmp->child[c] ? tmp->child[c] : tmp->child[c] = new Node());
            tmp->isValid = true;
        }
        vector<bool> f(s.size(), false);
        f[0] = true;
        for (int i = 0; i < s.size(); ++ i)
            if (f[i]) {
                Node* tmp = root;
                for (int j = i; j < s.size(); ++ j) {
                    if (!tmp->child[s[j]]) break;
                    tmp = tmp->child[s[j]];
                    f[j + 1] = f[j + 1] || tmp->isValid;
                }
            }
        return f[(int)s.size()];
    }
};
```

