class Solution {
    unordered_map<string, int> dict;
    vector<vector<int> > dp;
public:
    bool findSequence(int i, int j, string s, string curr){
        if(j >= s.size()){
            if(curr == "" or dict.find(curr) != dict.end()){
                return true;
            }
            else{
                return false;
            }
        }

        if(dp[i][j] != -1){
            return dp[i][j];
        }

        if(dict.find(curr + s[j]) != dict.end()){
            return dp[i][j] = findSequence(i, j+1, s, curr+s[j]) || findSequence(j+1, j+1, s, "");
        }
        else{
            return dp[i][j] = findSequence(i, j+1, s, curr+s[j]);
        }
    }

    bool wordBreak(string s, vector<string>& wordDict) {
        int n = s.size();
        for(int i=0;i<wordDict.size();i++){
            dict[wordDict[i]] = 1;
        }

        dp.resize(n+1, vector<int>(n+1, -1));
        return findSequence(0, 0, s, "");
    }
};
```

**2. Bottom Up DP Approach**

```
class Solution {
public:
    bool wordBreak(string s, vector<string>& wordDict) {
        if(wordDict.size() == 0)
            return false;

        unordered_set<string> dict(wordDict.begin(), wordDict.end());
        int longestWord = 0;
        for(string str: wordDict){
            longestWord = max(longestWord, (int)str.size());
        }

        int n = s.size();
        vector<bool> dp(n+1, false);
        dp[0] = true;

        for(int i = 1; i <= n; i++){
            for(int j = i-1; j >= max(i-longestWord, 0); j--){
                if(dp[j] == true){
                    if(dict.find(s.substr(j, i-j)) != dict.end()){
                        dp[i] = true;
                        break;
                    }
                }
            }
        }

        return dp[n];
    }
};
```

