class Solution:
    def dp(self, s, dic, dp):   #Top-Down
        if s == "":
            return True

        if s in dic:
            return True

        if s in dp:
            return dp[s]

        for i in range(1,len(s)+1):
            if s[:i] in dic and self.dp(s[i:],dic,dp):
                dp[s] = True
                return dp[s]

        dp[s] = False
        return dp[s]

    def wordBreak(self, s, dic):
        n = len(s)
        dp = {}
        return self.dp(s, dic,dp)
