struct TrieNode{
	TrieNode *children[26];
	bool isEnd;

	TrieNode()
	{
		for(int i=0; i<26; i++)
			children[i] = NULL;
		isEnd = false;
	}
};

TrieNode *root;
vector<bool> dp;

void insert(string word)
{
	int n = word.size();
	TrieNode *temp = root;
	for(int i=0; i<n; i++)
	{
		int x = (int)(word[i] - 'a');
		if(!temp->children[x])
			temp->children[x] = new TrieNode();
		temp = temp->children[x];
	}
	temp->isEnd = true;
}

void search(string word, int start)
{
	int n = word.size();
	TrieNode *temp = root;
	for(int i=start; i<n; i++)
	{
		int x = word[i] - 'a';
		if(!temp->children[x])
			return;
		temp = temp->children[x];
		if(temp->isEnd)
			dp[i+1] = 1;
	}

	if(temp->isEnd)
		dp[n] = 1;
}

