public:
	bool wordBreak(string s, vector<string>& A) {
		root = new TrieNode();
		int n = A.size(), l = s.size();
		dp.assign(l+1, 0);
		for(int i=0; i<n; i++)
			insert(A[i]);

		dp[0] = 1;
		for(int i=0; i<l; i++)
			if(dp[i])
				search(s, i);

		return dp[l];
	}
};

