class Solution {
    int n;
    vector<int> dp;

    bool dfs(int i, string s, vector<string>& wordDict){
        if(i == n)
            return true;

        if(dp[i] != -1)
            return dp[i];

        bool ans = false;
        for(auto word : wordDict){
            int sz = word.size();
            if(sz > n-i)
                continue;
            string newWord = s.substr(i, sz);

            if(word == newWord)
                ans |= dfs(i+sz, s, wordDict);

            dp[i] = ans;
            if(ans == true)
                return ans;
        }

        return ans;
    }
public:
    bool wordBreak(string s, vector<string>& wordDict) {
        n = s.size();

        dp = vector<int>(n, -1);
        return dfs(0, s, wordDict);
    }
};
