type VS = Vec<String>;
use std::collections::HashSet;
impl Solution {
    pub fn word_break(S: String, A: VS) -> bool {
        let seen = A.iter().map(|s| &s[..]).collect::<HashSet<&str>>();
        let N = S.len();
        let mut dp = vec![false; N + 1];       // 🤔 memo
        dp[N] = true;                          // 🛑 base case
        for i in (0..N).rev() {
            for k in 1..=N - i {
                dp[i] |= seen.contains(&&S[i..i + k]) && dp[i + k];  // 🎯 recurrence relation target
            }
        }
        dp[0]
    }
}
