class Solution {
    class Trie{
        boolean isWord;
        int height;
        Trie[] children;

        public Trie(){
            isWord = false;
            height = 0;
            children = new Trie[26];
        }

        public void add(String s){
            add(s, 0);
        }

        // Helper method for add
        public void add(String s, int pos){
            if (s.length() == pos){
                isWord = true;
                return;
            }
            int index = s.charAt(pos) - 'a';
            if (children[index] == null)
                children[index] = new Trie();
            children[index].add(s, pos+1);
            // Update the height of the tree
            height = Math.max(height, children[index].height+1);
        }

        // Check if the substring is present in the trie
        public boolean search(String s, int start, int end){
            // Return false if the string is longer than the height of the tree
            if (end - start > height)
                return false;
            if (start == end)
                return isWord;
            Trie child = children[s.charAt(start) - 'a'];
            if (child == null)
                return false;
            else 
                return child.search(s, start+1, end);
        }

    }

    public boolean wordBreak(String s, List<String> wordDict) {
        Trie trie = new Trie();
        for (String word: wordDict)
            trie.add(word);

        // Idea: s[0:i] can be separated if and only if s[0:j] can be separated
        //       and s[j:i] is present in the trie, for some j (0 <= j < i)
        boolean[] memo = new boolean[s.length()+1];
        memo[0] = true;
        for (int i = 1; i < s.length()+1; i++){
            for (int j = 0; j < i; j++){
                if (memo[j]){
                    if (trie.search(s, j, i))
                        memo[i] = true;
                }
            }
        }

        return memo[s.length()];
    }

}
```

