C++ version

    bool wordBreak(string s, unordered_set<string>& wordDict) {
      // Check Bad Input Values
      if(wordDict.size() == 0)
        return false;

      if (s.length() == 0)
      {
        return wordDict.find(s) != wordDict.end();
      }

      // Check the whole word first
      if (wordDict.find(s) != wordDict.end())
        return true;

      int n = s.size();
      // Suppose i is the start of a word and j it's end with 0 < i <= j< n
      // Lets create a table C (n*n) that check if s(i,j) is breakable
      // We have this formula for C[i,j]
      // C[i,j] = C[i,j-1] and s[j,j] is in the dictionary
      // C[i,j] = s(i,j) is in dictionary
      // C[i,j] = C[i,k] and s(k+1,j) is in dictonary, 0<k<j
      bool** C = new bool*[n];
      for(int i = 0; i < n; ++i)
      {
        C[i] = new bool[n];
        for(int j = 0; j<n; ++j)
        {
          // initialiazed to false;
          C[i][j] = false;
        }
      }

      // Fill the diagonal by checking if a word i,i is in the dictionary
      for (int i = 0; i < n; ++i)
      {
        C[i][i] = wordDict.find(s.substr(i,1)) != wordDict.end();
      }

      // Compute C[0,j] here
      for (int j = 1; j < n; ++j)
      {
        C[0][j] = C[0][j] ||
          (C[0][j - 1] && C[j][ j]) || // C[i,j-1] and s[j,j] is in the dictionary
          wordDict.find(s.substr(0, j + 1)) != wordDict.end();//s(i,j) is in dictionary
        if (!C[0][ j])
        {
          // Search for k: C[i,j] = C[i,k] and s(k+1,j) is in dictonary, 0<k<j
          int k = 0;
          while (k < j - 1 && !C[0][j])
          {
            C[k + 1][ j] = (wordDict.find(s.substr(k + 1, j - k)) != wordDict.end());
            C[0][ j] = C[0][ k] && C[k + 1][ j];
            ++k;
          }
        }
      }

      // Return true if s(0,n-1) is breakable
      bool result = C[0][n-1];

      // delete memory
      for(int i = 0; i < n; ++i)
      {
        delete [] C[i];
      }

      delete []C;
      C=NULL;

      return result;
    }

