class Solution {
public:
    bool solve(int ind,string str,set<string> st){
        if(ind==str.size()) return true;
        string temp="";
        for(int i=ind;i<str.size();i++){
            temp+=str[i];
            if(st.find(temp)!=st.end()){
                if(solve(i+1,str,st)){
                    return true;
                }
            }
        }
        return false;
