class Solution {
public:
    bool wordBreak(string s, vector<string>& wordDict) {
        int n=s.size();
        set<string> st;
        for(auto x:wordDict) st.insert(x);

        vector<bool> dp(n,false);
        dp[n]=true;

        for(int ind=n-1;ind>=0;ind--){
            string temp="";
            for(int i=ind;i<n;i++){
                temp+=s[i];
                if(st.find(temp)!=st.end()){
                    if(dp[i+1]){
                        dp[ind]=true;
                        break;
                    }
                }
            }

        }
        return dp[0];
    }
};
