// created a dp[pos], to store the boolean value of string being formed at that pos
int dp [301];
int helper(string s, vector<string> wordDict, int pos){
	// if we reach the end of string, we formed the string from the dict of wordDict
	if(pos==s.length()) return true;

	// if already saved in dp, return.
	if(dp[pos]!=-1) return dp[pos];

	string temp = "";
	for(int i =pos; i<s.length(); i++){
		// we keep adding in temp, and search if present in wordDict
		temp +=s[i];
		if(find(wordDict.begin(), wordDict.end(),temp)!=wordDict.end()){
			if(helper(s, wordDict, i+1))    return true;            
		}
	}
	return dp[pos] =  false;        
}

bool wordBreak(string s, vector<string>& wordDict) {
	memset(dp, -1, sizeof(dp));
	return helper(s, wordDict, 0);
}
```

