    int solve(int idx,map<string,int>&m,string &s){
        if(idx<0)return 1;
        if(dp[idx]!=-1)return dp[idx];
        cout<<idx<<endl;
        string str;
        int ans=0;
        for(int i=idx;i>=0;i--){
            str.push_back(s[i]);
            string temp=str;
            reverse(temp.begin(),temp.end());
            // cout<<temp<<endl;

            if(m[temp]==1){
                ans+=solve(i-1,m,s);
            }
        }
       if(ans>0)return dp[idx]=1;
       else return dp[idx]=0;
    }
    bool wordBreak(string s, vector<string>& wordDict) {
        map<string,int>m;
        for(auto it:wordDict){
            m[it]=1;
        }
        vector<int>dp(s.size(),-1);
        return solve(s.size()-1,m,s,dp);
    }
};

