public:
    unordered_set<string> words;
    vector<int> dp;

    bool wordBreak(string s, vector<string>& wordDict) {
        for(auto x: wordDict) words.insert(x);
        int n = s.length();
        dp.resize(n+1, -1); // -1 indicates that the state has not been solved yet
        return helper(0, s);
    }

    bool helper(int start, string s){
        if(start == s.length()) return true;
        if(dp[start] != -1) return dp[start];
        for(int end = start+1; end <= s.length(); end++){
            if(words.find(s.substr(start, end-start)) != words.end() && helper(end, s)){
                dp[start] = 1;
                return true;
            }
        }
        dp[start] = 0;
        return false;
    }
};
