Do you remember the [Min Stack][1] problem? In this problem, a similar `Max Queue` is needed!

Here is my 104ms c++ solution.

    class Solution {
    public:
    	std::vector<int> maxSlidingWindow(std::vector<int>& nums, int k) {
    		std::vector<int> res;
    		if (k < 1)
    			return res;
    		MaxQueue mq;
    		for (int i = 0; i != k; ++i)
    			mq.push(nums[i]);
    		res.push_back(mq.getMax());
    		for (int i = k; i != nums.size(); ++i) {
    			mq.pop();
    			mq.push(nums[i]);
    			res.push_back(mq.getMax());
    		}
    		return res;
    	}
    private:
    	class MaxQueue {
    	public:
    		void push(int x) {
    			nums.push(x);
    			while (!maxs.empty() && maxs.back() < x)
    				maxs.pop_back();
    			maxs.push_back(x);
    		}
    		void pop() {
    			if (nums.front() == maxs.front())
    				maxs.pop_front();
    			nums.pop();
    		}
    		int getMax() {
    			return maxs.front();
    		}
    	private:
    		std::queue<int> nums;
    		std::deque<int> maxs;
