class Solution {
public:
    vector<int> maxSlidingWindow(vector<int>& arr, int k) {
        vector<int> res;
        deque<int> list;
        int n = arr.size();
        int i = 0;
        int j = 0;

        while(j < n){

            while(!list.empty() && list.back() < arr[j]){
                // cout << "popping from the list... " << list.back() << endl;
                list.pop_back();
