            while dq and nums[dq[-1]] <= nums[i]:
                dq.pop()

            # Push cur element
            dq.append(i)

            # Check if there is out of window element at front
            if dq[0] == i - k:
                dq.popleft()

            # Get max element for cur window
            if i >= k - 1:
                ans.append(nums[dq[0]])

        return ans
