class Solution {
public:
    vector<int> nextGreaterElement(vector<int> nums)
    {
        stack<int> st;
        vector<int> Index(nums.size(), 0);

        for(int idx = nums.size() - 1; idx >= 0; idx--)
        {
            while(!st.empty() and nums[idx] > nums[st.top()])
                st.pop();

            if(st.empty()) Index[idx] = nums.size();
            else Index[idx] = st.top();

            st.push(idx);
        }

        return Index;
    }

    vector<int> maxSlidingWindow(vector<int>& nums, int k) 
    {
         vector<int> Index = nextGreaterElement(nums);
         vector<int> ans;

         int curr = 0;

         for(int idx = 0; idx <= nums.size() - k; idx++)
         {
             if(curr < idx) curr = idx;

             while(Index[curr] < idx + k)
                 curr = Index[curr];             //updating index

             ans.push_back(nums[curr]);
         }

         return ans;
    }
};
