class Solution:
    def maxSlidingWindow(self, nums: List[int], k: int) -> List[int]:
        if not nums:
        	return []

        l = len(nums)
        if k > l:
        	k = l

        # monotonously decreasing queue
        mono_queue = collections.deque()
        rst = []

        for idx, num in enumerate(nums):
            # maintain the monotonously decreasing property
            while mono_queue and nums[mono_queue[-1]] <= num:
                mono_queue.pop()

            # push current index onto the queue
            mono_queue.append(idx)

            # check whether currently the largest element in the queue (element
            # at the front) is within the window or not
            while mono_queue and mono_queue[0] <= idx - k:
                mono_queue.popleft()

            if idx >= k - 1:
                rst.append(nums[mono_queue[0]])

        return rst
