class Solution {
    public int[] maxSlidingWindow(int[] nums, int k) {
        int n=nums.length;  //length of array
        int m=0; //counter for ans array
        int[] ans=new int[n-k+1]; //array to store output
        TreeMap<Integer, Integer> treeMap=new TreeMap<>(); //<Element,Frequency>
        for(int i=0;i<n;i++){   //iterate over array 

            /*add element at ith index of array in treeMap. 
            If no freq mapped(naya element aaya), it take default value 0 and increament one. 
            If element already exists, just increament the frequency by 1*/
            treeMap.put(nums[i],treeMap.getOrDefault(nums[i],0)+1);  

            if(i>=k-1){    //if k=3, toh teen element is in tree map so now we can perform the necessary operations

                //Treemap has data in sorted fashion so extract the last key which is also maximux
                ans[m++]=treeMap.lastKey();

                //from the Starting, one element has to be removed(i-k+1) so that another can be taken into consideration, so we decreament its frequency
                treeMap.put(nums[i-k+1],treeMap.get(nums[i-k+1])-1);

                //if frequency of(i-k+1)th element becomes zero, we simply remove that element.
                if(treeMap.get(nums[i-k+1])==0)
                    treeMap.remove(nums[i-k+1]);

            }
        }
        return ans;
    }
}
