class Solution {
    public int[] maxSlidingWindow(int[] nums, int k) {

        // Priority Queue =========================================================
        PriorityQueue<int[]> pq = new PriorityQueue<>((a, b) -> b[0] - a[0]);
        for (int i = 0; i < k; ++i) pq.add(new int[]{nums[i], i});
        int[] ans = new int[nums.length - k + 1];
        ans[0] = pq.peek()[0];
        int j = 1;
        for (int i = k; i < nums.length; ++i) {
            pq.add(new int[]{nums[i], i});
            while (pq.peek()[1] <= i - k)
                pq.poll();
            ans[j++] = pq.peek()[0];
        }
        return ans;

        // Dequeue ================================================================
        if (nums == null || k < 0) return new int[0];
        int n = nums.length;
        int[] arr = new int[n - k + 1];
        // stores index
        Deque<Integer> que = new ArrayDeque<>();
        int j = 0;
        for (int i = 0; i < n; ++i) {
            // remove numbers from start if they are out of range
            while (!que.isEmpty() && que.peek() < i - k + 1)
                que.poll();
            // remove numbers from last if they are smaller than current no. bcz they are useless (just like me)
            while (!que.isEmpty() && nums[que.peekLast()] < nums[i])
                que.pollLast();
            // put current index in queue
            que.offer(i);
            if (i >= k - 1)
                arr[j++] = nums[que.peek()];
        }
        return arr;
    }

	// DP =====================================================================
        if (nums == null || k < 0) return new int[0];
        int n = nums.length;
        int[] left = new int[n];
        left[0] = nums[0];
        int[] right = new int[n];
        right[n - 1] = nums[n - 1];
        for (int i = 1; i < n; ++i) {
            if (i % k == 0) {
                left[i] = nums[i];
            } else {
                left[i] = Math.max( nums[i], left[i - 1] );
            }

            int j = n - 1 - i;
            if ((j + 1) % k == 0) {
                right[j] = nums[j];
            } else {
                right[j] = Math.max( nums[j], right[j + 1] );
            }
        }
        int[] ans = new int[n - k + 1];
        for (int i = 0; i < n - k + 1; ++i)
            ans[i] = Math.max( right[i], left[i + k - 1] );
        return ans;
}
