class Solution {
public:
    vector<int> maxSlidingWindow(vector<int>& nums, int k) {
        priority_queue<pair<int,int>>pq;
        vector<int>res;
        int ind=0;
        for(ind=0;ind<k;ind++){
            pq.push({nums[ind],ind});
        }
        res.push_back(pq.top().first);
        for(int i=ind;i<nums.size();i++){
            pq.push({nums[i],i});
            while(pq.top().second<=i-k) pq.pop();
            res.push_back(pq.top().first);
        }
        return res;
    }
};
