    vector<int> maxSlidingWindow(vector<int>& nums, int k) {
        int n=nums.size(); // n=8, k=3
        deque<pair<int, int>> deque;
        vector<int> result;

        for(int i=0; i<k; i++){
            while(!deque.empty() && deque.back().second<nums[i]) // 3, -1
                deque.pop_back();
            deque.push_back({i, nums[i]}); // 1(3), 2(-1)
        }
        result.push_back(deque.front().second); // 3   

        for(int i=k; i<n; i++){
            while(!deque.empty() && deque.back().second<nums[i]) // 7
                deque.pop_back();
            while(!deque.empty() && deque.front().first<i-k+1) // 2(-1), 3(-3)
                deque.pop_front();

            deque.push_back({i, nums[i]}); // 3(-3)
            result.push_back(deque.front().second); // 2(-1)
        }
        return result;
    }
};

