class Solution:
    def maxSlidingWindow(self, nums: List[int], k: int) -> List[int]:
        if not nums:
            return []
        cnt = collections.defaultdict(int)
        heap = []
        for i in range(k):
            heapq.heappush(heap,-nums[i])
        res = [-heap[0]]
        for i in range(1,len(nums)-k+1):
            heapq.heappush(heap,-nums[i+k-1])
            cnt[-nums[i-1]] += 1
            while cnt[heap[0]] > 0:
                cnt[heap[0]] -= 1
                heapq.heappop(heap)
            res += [-heap[0]]
        return res
