    public int[] maxSlidingWindow(int[] nums, int k) {
        if(nums.length == 0)
            return nums;
        int[] res = new int[nums.length - k + 1];
        Deque<Integer> memo = new ArrayDeque<>();
        for(int i = 0  ; i < nums.length ; i++){
            while(memo.size() > 0 && memo.getLast() < nums[i])
                memo.removeLast();
            memo.add(nums[i]);
            if(i < k - 1)
                continue;
            res[i - k + 1] = memo.peek();
            if(nums[i - k + 1] == res[i - k + 1])
                memo.removeFirst();
        }
        return res;
    }

