class Solution {
public:
    vector<int> maxSlidingWindow(vector<int>& nums, int k) {
        vector<int>ans;
        priority_queue<pair<int,int>>pq;
        // i is start and j is end 
        int i=0,j=0;
        while(j<nums.size())
        {
            pq.push({nums[j],j});
            if(j-i+1==k)
            {
                // j-i+1 is window size 
                bool done=false;
                while(!done)
                {
                    int top_ind=pq.top().second;
                    // check top element index is valid or not
                    // valid => push top element first data in answer 
                    // invalid => pop top element
                    if(top_ind>=i && top_ind<=j)
                    {
                        done=true;
                        ans.push_back(pq.top().first);
                    }
                    else
                    pq.pop();
                }
                if(pq.top().second==i)
                pq.pop();
                i++;
            }
            j++;
        }
        return ans;
    }
};

```

