        for(int i=0;i<nums.size();i++){
            if(!q.empty() and q.front()==i-k) q.pop_front();
            while(!q.empty() and nums[i]>nums[q.back()]) q.pop_back();
            q.push_back(i);
            if(i>=k-1) v.push_back(nums[q.front()]);
        }
        return v;
    }
};
