        ArrayList<Integer> list = new ArrayList<> ( );
        int[] ans = new int[nums.length - k + 1];
        int start = 0;
        int length = 0;
        for(int i = 0; i<nums.length; i++) {
            list.add(nums[i]);
            while(list.size() == k ) { 
                ans[start] = (Collections.max(list));
                list.remove(0);
                start++;
            }
        }
        return ans;
    }
}

class Solution {
public:
    vector<int> maxSlidingWindow(vector<int>& arr, int k) {
         int n=arr.size();
          int i=0,j=0;
        vector<int>ans;
          priority_queue<int>pq;
          while(j<=n)
          {
                    if(j-i+1<=k){
                              pq.push(arr[j]);
                              j++;
                    }
                    else
                    {
                              ans.push_back(pq.top());
                              if(arr[i]==pq.top())pq.pop();
                              i++;
                    }
          }
          return ans;
    }
};



