class Solution(object):
    def firstMissingPositive(self, nums):
        """
        :type nums: List[int]
        :rtype: int
        """
        temp, N = [None]*len(nums), len(nums)
        for x in nums:
            if 1<=x<=N:
                temp[x-1] = x
        for i in range(N):
            if temp[i] == None:
                return i+1
        return N+1
