    {
        if(nums[i] > 0 && nums[i] != 0)
        {
          nums[indexPos++] = nums[i];
        }
    }
      int negativeIndex = indexPos;
      for(int j = indexPos; j < nums.Length; j++)
          nums[indexPos] = -1;
      if(nums[0] != 1) return 1;
      for(int i = 0 ; i < negativeIndex - 1; i++)
      {
          if(nums[i + 1] != nums[i] + 1 && nums[i+1] != nums[i])
              return nums[i] + 1;
      }
    return maxNumber + 1;
  }
}

