class Solution {
public:
    int firstMissingPositive(vector<int>& nums) {
        /*

        Lets say the size of the array is n
        We need to find the first missing positive number
        The range of values for this required number should be ? 
        If the array is strictly increasing and starts with 1 then the missing number would be n+1;
        but if thats not the case then the first positive number should be in the range[1,n];

        algo should be:
        we can discard the negative and numbers greater than n.
        For the remaining numbers we can use them as index and
        mark the number in index position. 
         One way to mark would be first replace negative number and number greater than n with n+1
         Then using marking technique mentioned convert the value to negative.

        After that iterate the array and return the first unmarked poition +1 value as the result.
        */

        if(nums.size()==1 && nums[0]<=0)
            return 1;

        for(int i=0;i<nums.size();i++){
            if(nums[i]<=0 || nums[i] > nums.size()+1)
                nums[i]=nums.size()+1;            
        }

        for(int i=0;i<nums.size();i++){
            if(abs(nums[i]) > nums.size())continue;
            int idx = abs(nums[i])-1;

            if( nums[idx] > 0)
                nums[idx]=-nums[idx];
        }
        for(int i=0;i<nums.size();i++){
            if(nums[i]>0)
                return i+1;
        }
        return nums.size()+1;
    }
};
