class Solution {
public:
    int firstMissingPositive(vector<int>& nums) {

        int n = nums.size();

		// Checking if the answer is 1
        bool f = false;
        for(int i = 0; i < n; i++)
        {
            if(nums[i] == 1)
            {
                f = true;
                break;
            }
        }

        if(!f)
        {
            return 1;
        }
        // 1 is present so it doesn't matter how many times it is present

		// Making all the out of bound elements 1
        for(int i = 0; i < n; i++)
        {
            if(nums[i] < 1 || nums[i] > n)
            {
                nums[i] = 1;
            }
        }

		// Hashing
        for(int i = 0; i < n; i++)
        {
            int c = abs(nums[i]);
            nums[c - 1] = -1 * abs(nums[c - 1]);
        }

		// Checking
        for(int i = 0; i < n; i++)
        {
            if(nums[i] > 0)
            {
                return i + 1;
            }
        }


        return n + 1;
    }
};
